	//关闭自动填充
	$('input').attr('autocomplete', 'off');

	var sm = new Vue({
		el: "#accountSet",
	    data:{
	     acts:{'基本信息':'#basicInfor','账号密码':'#accout','更多设置':'#newsNotice'},
	     changeEmail:true,
	     alterEmail:false,
	     changePwd:true,
	     alterPwd:false,
	     listen:false,
	    },
	    methods:{
	    	chooseImg:function(){
	    		$('#upImg').click();
	    	},
	    	changeBasic:function(){
	    		$('.saveInfor').eq(0).removeAttr('disabled');
	    	},
	    	changeAccount:function(){
	    		$('.saveInfor').eq(1).removeAttr('disabled');
	    	},
	    	//获取图片路径
			  getImgUrl:function(file) { 
			　　var url = null ; 
			　　if (window.createObjectURL!=undefined) { 
			　　　　url = window.createObjectURL(file) ; 
			　　} else if (window.URL!=undefined) { 
			　　　　url = window.URL.createObjectURL(file) ; 
			　　} else if (window.webkitURL!=undefined) { 
			　　　　url = window.webkitURL.createObjectURL(file) ; 
			　　} 
			　　return url ; 
			  },
			//预览
			   preview:function (e) {
			   		var file = e.target.files[0];
			     	var url = this.getImgUrl(file);
				   if(url){
				   	 $('.uImg').attr('src',url);
				   }
			   },
			//更改邮箱
			  updateEmail:function(){
			  	this.changeEmail = false;
			  	this.alterEmail = true;
			  	this.listen = true;
			  },
			//更改密码
			  updatePwd:function(){
			  	this.changePwd = false;
			  	this.alterPwd = true;
			  	this.listen = true;
			  },
			 //取消所有操作
			 cancelUtil:function(){
			 	 this.changeEmail=true;
			     this.alterEmail=false;
			     this.changePwd=true;
			     this.alterPwd=false;
			     this.listen=false;
			     $('.saveInfor').eq(1).attr('disabled', 'disabled');
			 },
	    },
		delimiters:['[[',']]'],
	});
	//表单验证
		function validform(e){
			var form = e.Validform({
			    tiptype:2,
			showAllError:true,
			});
		}
		validform($('.basic-form'));
		validform($('.account-form'));
