
	    $('#resendEmail').on('click', function(event) {
	    	$('.success').hide();
	    	$('.resend').show();
	    });	   
	    //确认提交
	    $('#handle-btn').click(function(event) {    	
	    	var email = /^[a-zA-Z0-9_-]+@[a-zA-Z0-9_-]+(\.[a-zA-Z0-9_-]+)+$/;
	    	var pwd = /^[a-zA-Z0-9]{6,16}/;
	    	if(email.test($('#email').val())&&pwd.test($('#password').val())){
	    		$.ajax({
	    			url:'/user/send/email/activeAccount/',
	    			type:'POST',
	    			datatype:'text',
	    			data:$('#resendForm').serialize(),
	    			success:function(con){	
	    				if(con=='发送成功'){showMsg('<strong>发送成功!</strong>请前往你的邮箱查看');close_current_page(5000);}
	    				else{showMsg(con);}
	    			},
	    			error:function(){
	    				showMsg('请求失败,请稍后再试');
	    			},
	    		});
	    	}
	    	else{
	    		showMsg('密码或邮箱不符合要求!');
	    	}
	    	
	    });