    
    //页面滚动条触发
	window.onscroll = function(e){
		var t=document.documentElement.scrollTop||document.body.scrollTop;
		var h = $('#head_bar1');
		if(t>200){h.css({'top': '0','background-color': '#1EA47D','position':'fixed','box-shadow':'0 1px 10px #4A4949'});}
		else{h.css({'top': '20px','background-color': 'transparent','position':'absolute','box-shadow':'none'});}
		if(t>350){$('.short-msg').show();$('.utilImg').show();}
		if(t>600){$('.stepCon').show();$('.goto-top').show();}else{$('.goto-top').hide();}
	}
	var fm = new Vue({
		el: "#feature-bar",
		data:{
  			seen1:true,
  			seen2:false,
  			
  		},
  		delimiters:['[[',']]'],//更改vue的语法为[[]],避免与django冲突
		methods:{
			changImg(){
				this.seen1 = this.seen1?false:true;
				this.seen2 = this.seen2?false:true;

			}
		}
	});
	setInterval(fm.changImg,4000);

